import React, { useState, useEffect } from 'react';
import axios from 'axios';
import config from './config'; // Import the configuration
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // Create and import a custom CSS file for additional styling
import { MapContainer, TileLayer, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const App = () => {
  const [route, setRoute] = useState([]);
  const [airlineId, setAirlineId] = useState('1'); // Example airline ID
  const [currentCoords, setCurrentCoords] = useState({ lat: 0, lon: 0 }); // Renamed from location

  useEffect(() => {
    fetchRoute();
  }, [airlineId]);

  const fetchRoute = async () => {
    try {
      const response = await axios.get(`http://127.0.0.1:5000/route_by_airline`, {
        params: { airline_id: airlineId }
      });
      if (response.data.waypoints) {
        setRoute(response.data.waypoints);
      }
    } catch (error) {
      console.error("Error fetching route data", error);
    }
  };

  const requestAlternativeRoute = async () => {
    try {
      const response = await axios.post(`${config.backendUrl}/alternative-route`, { current_coords: currentCoords, planned_route: route });
      setRoute(response.data.route);
    } catch (error) {
      console.error("Error fetching alternative route", error);
    }
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Pilot Interface</h1>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Route</h2>
          <MapContainer center={[0, 0]} zoom={2} style={{ height: '400px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <Polyline positions={route.map(point => [point.lat, point.lon])} color="blue" />
          </MapContainer>
        </div>
      </div>
      <button className="btn btn-primary" onClick={requestAlternativeRoute}>Request Alternative Route</button>
    </div>
  );
};

export default App;
