import React, { useState, useEffect } from 'react';
import axios from 'axios';
import config from './config';
import 'bootstrap/dist/css/bootstrap.min.css';
import { MapContainer, TileLayer, Polyline, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import './App.css';
import L from 'leaflet';

const App = () => {
  const [routes, setRoutes] = useState([]);

  useEffect(() => {
    fetchAllRoutes();
  }, []);

  const fetchAllRoutes = async () => {
    try {
      const response = await axios.get(`${config.backendUrl}/all_routes`);
      console.log("Fetched routes:", response.data);  // Debug log
      setRoutes(response.data);
    } catch (error) {
      console.error("Error fetching all routes", error);
    }
  };

  const createCustomMarkerIcon = () => {
    return new L.Icon({
      iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
      iconSize: [25, 41],
      iconAnchor: [12, 41],
      popupAnchor: [1, -34],
      shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
      shadowSize: [41, 41]
    });
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">ATC Interface</h1>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">All Flight Paths</h2>
          <MapContainer center={[20, 0]} zoom={2} style={{ height: '600px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {routes.map((route, index) => (
              <Polyline
                key={index}
                positions={[
                  [route.source.lat, route.source.lon],
                  [route.destination.lat, route.destination.lon],
                ]}
                color="red"
              >
                <Marker position={[route.source.lat, route.source.lon]} icon={createCustomMarkerIcon()}>
                  <Popup>{route.source.name}</Popup>
                </Marker>
                <Marker position={[route.destination.lat, route.destination.lon]} icon={createCustomMarkerIcon()}>
                  <Popup>{route.destination.name}</Popup>
                </Marker>
              </Polyline>
            ))}
          </MapContainer>
        </div>
      </div>
    </div>
  );
};

export default App;
