import React, { useState, useEffect } from 'react';
import axios from 'axios';
import config from './config'; // Import the configuration
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // Create and import a custom CSS file for additional styling
import { MapContainer, TileLayer, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const App = () => {
  const [location, setLocation] = useState({ lat: 0, lon: 0 });
  const [weather, setWeather] = useState({});
  const [route, setRoute] = useState([]);
  const [sourceAirportId, setSourceAirportId] = useState(2990); // Example source airport ID
  const [destinationAirportId, setDestinationAirportId] = useState(2966); // Example destination airport ID

  useEffect(() => {
    fetchWeather();
    fetchRoute();
  }, [location, sourceAirportId, destinationAirportId]);

  const fetchWeather = async () => {
    try {
      const response = await axios.get(`${config.backendUrl}/weather?lat=${location.lat}&lon=${location.lon}`);
      setWeather(response.data);
    } catch (error) {
      console.error("Error fetching weather data", error);
    }
  };

  const fetchRoute = async () => {
    try {
      const response = await axios.get(`${config.backendUrl}/route?source_airport_id=${sourceAirportId}&destination_airport_id=${destinationAirportId}`);
      if (response.data.waypoints) {
        setRoute(response.data.waypoints);
      }
    } catch (error) {
      console.error("Error fetching route data", error);
    }
  };

  const requestAlternativeRoute = async () => {
    try {
      const response = await axios.post(`${config.backendUrl}/alternative-route`, { current_coords: location, planned_route: route });
      setRoute(response.data.route);
    } catch (error) {
      console.error("Error fetching alternative route", error);
    }
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Pilot Interface</h1>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Current Location</h2>
          <p>Latitude: {location.lat}</p>
          <p>Longitude: {location.lon}</p>
        </div>
      </div>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Weather</h2>
          <pre>{JSON.stringify(weather, null, 2)}</pre>
        </div>
      </div>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Route</h2>
          <MapContainer center={[0, 0]} zoom={2} style={{ height: '400px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <Polyline positions={route.map(point => [point.lat, point.lon])} color="blue" />
          </MapContainer>
        </div>
      </div>
      <button className="btn btn-primary" onClick={requestAlternativeRoute}>Request Alternative Route</button>
    </div>
  );
};

export default App;
