import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { MapContainer, TileLayer, Polyline, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import './App.css';
import L from 'leaflet';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import './CustomMarkerIcon.css';

const config = {
  backendUrl: 'http://localhost:5000/api'
};

const App = () => {
  const [route, setRoute] = useState([]);
  const [currentCoords, setCurrentCoords] = useState({ lat: 0, lon: 0 });
  const [weather, setWeather] = useState(null);
  const [changeRequests, setChangeRequests] = useState([]);
  const [approvedRoute, setApprovedRoute] = useState([]);

  const airlineId = '410';
  const sourceAirportId = '2965';
  const destinationAirportId = '2990';

  useEffect(() => {
    fetchRoute();
  }, []);

  useEffect(() => {
    if (currentCoords.lat && currentCoords.lon) {
      fetchWeather(currentCoords.lat, currentCoords.lon);
    }
  }, [currentCoords]);

  const fetchRoute = async () => {
    try {
      const response = await axios.get(`${config.backendUrl}/route_by_airline`, {
        params: {
          airline_id: airlineId,
          source_airport_id: sourceAirportId,
          destination_airport_id: destinationAirportId
        }
      });
      if (response.data.waypoints) {
        setRoute(response.data.waypoints);
        setCurrentCoords(response.data.waypoints[0]);
      } else {
        console.error("Error fetching route data: No waypoints found");
      }
    } catch (error) {
      console.error("Error fetching route data", error);
    }
  };

  const fetchWeather = async (lat, lon) => {
    try {
      const response = await axios.get(`${config.backendUrl}/weather`, {
        params: { lat, lon }
      });
      setWeather(response.data);
    } catch (error) {
      console.error("Error fetching weather data", error);
    }
  };

  const requestAlternativeRoute = async () => {
    try {
      const response = await axios.post(`${config.backendUrl}/request-change-route`, {
        current_coords: currentCoords,
        planned_route: route,
        destination_airport_id: destinationAirportId
      });
      console.log(response.data.message);
    } catch (error) {
      console.error("Error requesting alternative route", error);
    }
  };

  const fetchChangeRequests = async () => {
    try {
      const response = await axios.get(`${config.backendUrl}/change-requests`);
      setChangeRequests(response.data);
    } catch (error) {
      console.error("Error fetching change requests", error);
    }
  };

  const approveChangeRequest = async (requestId) => {
    try {
      const response = await axios.post(`${config.backendUrl}/approve-change-request`, {
        request_id: requestId
      });
      console.log(response.data.message);
      setApprovedRoute(response.data.new_route);
    } catch (error) {
      console.error("Error approving change request", error);
    }
  };

  const createCustomMarkerIcon = () => {
    return L.divIcon({
      html: `<LocationOnIcon style="color: red; font-size: 32px;" />`,
      iconSize: [32, 32],
      className: 'custom-marker-icon',
    });
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Pilot Interface</h1>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Route</h2>
          <MapContainer center={[currentCoords.lat, currentCoords.lon]} zoom={5} style={{ height: '400px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {route.length > 0 && (
              <>
                <Polyline
                  positions={route.map(coord => [coord.lat, coord.lon])}
                  color="blue"
                />
                {route.map((coord, index) => (
                  <Marker key={index} position={[coord.lat, coord.lon]} icon={createCustomMarkerIcon()}>
                    <Popup>
                      {index === 0 ? 'Source Airport: ' + coord.name : index === route.length - 1 ? 'Destination Airport: ' + coord.name : `Waypoint ${index}`}
                    </Popup>
                  </Marker>
                ))}
              </>
            )}
          </MapContainer>
          <button
            className="btn btn-primary mt-4"
            onClick={requestAlternativeRoute}
          >
            Request Alternative Route
          </button>
        </div>
      </div>

      {weather && (
        <div className="card mb-4">
          <div className="card-body">
            <h2 className="card-title">Current Weather</h2>
            <p><strong>Temperature:</strong> {weather.main.temp} K</p>
            <p><strong>Weather:</strong> {weather.weather[0].description}</p>
            <p><strong>Humidity:</strong> {weather.main.humidity}%</p>
            <p><strong>Wind Speed:</strong> {weather.wind.speed} m/s</p>
          </div>
        </div>
      )}

      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Change Requests</h2>
          <button className="btn btn-secondary mb-4" onClick={fetchChangeRequests}>Fetch Change Requests</button>
          {changeRequests.length > 0 && (
            <ul className="list-group">
              {changeRequests.map((request, index) => (
                <li key={index} className="list-group-item">
                  <p>Request ID: {request._id}</p>
                  <p>Destination Airport ID: {request.destination_airport_id}</p>
                  <button className="btn btn-success" onClick={() => approveChangeRequest(request._id)}>Approve Change</button>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Approved Route</h2>
          <MapContainer center={[currentCoords.lat, currentCoords.lon]} zoom={5} style={{ height: '400px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {approvedRoute.length > 0 && (
              <>
                <Polyline
                  positions={approvedRoute.map(coord => [coord.lat, coord.lon])}
                  color="green"
                />
                {approvedRoute.map((coord, index) => (
                  <Marker key={index} position={[coord.lat, coord.lon]} icon={createCustomMarkerIcon()}>
                    <Popup>
                      {index === 0 ? 'Source Airport: ' + coord.name : index === approvedRoute.length - 1 ? 'Destination Airport: ' + coord.name : `Waypoint ${index}`}
                    </Popup>
                  </Marker>
                ))}
              </>
            )}
          </MapContainer>
        </div>
      </div>
    </div>
  );
};

export default App;
