import React, { useState, useEffect } from 'react';
import axios from 'axios';
import config from './config';
import 'bootstrap/dist/css/bootstrap.min.css';
import { MapContainer, TileLayer, Polyline, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import './App.css';

const App = () => {
  const [route, setRoute] = useState([]);
  const [currentCoords, setCurrentCoords] = useState({ lat: 0, lon: 0 }); // Current location

  const airlineId = '410';
  const sourceAirportId = '2965';
  const destinationAirportId = '2990';

  useEffect(() => {
    fetchRoute();
  }, []);

  const fetchRoute = async () => {
    try {
      const response = await axios.get(`${config.backendUrl}/route_by_airline`, {
        params: {
          airline_id: airlineId,
          source_airport_id: sourceAirportId,
          destination_airport_id: destinationAirportId
        }
      });
      if (response.data.waypoints) {
        setRoute(response.data.waypoints);
        setCurrentCoords(response.data.waypoints[0]); // Set the initial current coordinates
      } else {
        console.error("Error fetching route data: No waypoints found");
      }
    } catch (error) {
      console.error("Error fetching route data", error);
    }
  };

  const requestAlternativeRoute = async () => {
    try {
      const response = await axios.post(`${config.backendUrl}/alternative-route`, {
        current_coords: currentCoords,
        planned_route: route
      });
      setRoute(response.data.route);
    } catch (error) {
      console.error("Error fetching alternative route", error);
    }
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Pilot Interface</h1>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Route</h2>
          <MapContainer center={[currentCoords.lat, currentCoords.lon]} zoom={5} style={{ height: '400px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {route.length > 0 && (
              <>
                <Polyline
                  positions={route.map(coord => [coord.lat, coord.lon])}
                  color="blue"
                />
                {route.map((coord, index) => (
                  <Marker key={index} position={[coord.lat, coord.lon]}>
                    <Popup>
                      {index === 0 ? 'Starting Point' : index === route.length - 1 ? 'Destination' : `Waypoint ${index}`}
                    </Popup>
                  </Marker>
                ))}
              </>
            )}
          </MapContainer>
          <button
            className="btn btn-primary mt-4"
            onClick={requestAlternativeRoute}
          >
            Request Alternative Route
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
