import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

function App() {
  const [route, setRoute] = useState(null);
  const airlineId = 410;
  const sourceAirportId = 2965;
  const destinationAirportId = 2990;

  useEffect(() => {
    const fetchRoute = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:5000/route_by_airline`, {
          params: {
            airline_id: airlineId,
            source_airport_id: sourceAirportId,
            destination_airport_id: destinationAirportId
          }
        });
        setRoute(response.data);
      } catch (error) {
        console.error('Error fetching route data', error);
      }
    };

    fetchRoute();
  }, []);

  return (
    <div>
      <h1>Pilot Interface</h1>
      {route ? (
        <MapContainer center={[route.source_airport.latitude, route.source_airport.longitude]} zoom={6} style={{ height: "600px", width: "100%" }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          />
          <Marker position={[route.source_airport.latitude, route.source_airport.longitude]}>
          </Marker>
          <Marker position={[route.destination_airport.latitude, route.destination_airport.longitude]}>
          </Marker>
          <Polyline
            positions={[
              [route.source_airport.latitude, route.source_airport.longitude],
              [route.destination_airport.latitude, route.destination_airport.longitude]
            ]}
            color="blue"
          />
        </MapContainer>
      ) : (
        <p>Loading route...</p>
      )}
    </div>
  );
}

export default App;
