import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { MapContainer, TileLayer, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const App = () => {
  const [route, setRoute] = useState([]);
  const [sourceAirportId, setSourceAirportId] = useState(3484); // Example source airport ID
  const [destinationAirportId, setDestinationAirportId] = useState(3485); // Example destination airport ID

  useEffect(() => {
    fetchRoute();
  }, [sourceAirportId, destinationAirportId]);

  const fetchRoute = async () => {
    try {
      const response = await axios.get(`http://127.0.0.1:5000/route`, {
        params: {
          source_airport_id: sourceAirportId,
          destination_airport_id: destinationAirportId
        }
      });
      if (response.data.waypoints) {
        setRoute(response.data.waypoints);
      }
    } catch (error) {
      console.error("Error fetching route data", error);
    }
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Pilot Interface</h1>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Route</h2>
          <MapContainer center={[0, 0]} zoom={2} style={{ height: '400px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <Polyline positions={route.map(point => [point.lat, point.lon])} color="blue" />
          </MapContainer>
        </div>
      </div>
    </div>
  );
};

export default App;
