import React, { useState, useEffect } from 'react';
import axios from 'axios';
import config from './config';
import 'bootstrap/dist/css/bootstrap.min.css';
import { MapContainer, TileLayer, Polyline, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import './App.css';
import L from 'leaflet';

const App = () => {
  const [route, setRoute] = useState([]);
  const [currentCoords, setCurrentCoords] = useState({ lat: 0, lon: 0 });
  const [weather, setWeather] = useState(null);
  const [approvedRoute, setApprovedRoute] = useState(null);

  const airlineId = '410';
  const sourceAirportId = '2965';
  const destinationAirportId = '2990';

  useEffect(() => {
    fetchRoute();
  }, []);

  useEffect(() => {
    if (currentCoords.lat && currentCoords.lon) {
      fetchWeather(currentCoords.lat, currentCoords.lon);
    }
  }, [currentCoords]);

  const fetchRoute = async () => {
    try {
      const response = await axios.get(`${config.backendUrl}/route_by_airline`, {
        params: {
          airline_id: airlineId,
          source_airport_id: sourceAirportId,
          destination_airport_id: destinationAirportId
        }
      });
      if (response.data.waypoints) {
        setRoute(response.data.waypoints);
        setCurrentCoords(response.data.waypoints[0]);
      } else {
        console.error("Error fetching route data: No waypoints found");
      }
    } catch (error) {
      console.error("Error fetching route data", error);
    }
  };

  const fetchWeather = async (lat, lon) => {
    try {
      const response = await axios.get(`${config.backendUrl}/weather`, {
        params: { lat, lon }
      });
      setWeather(response.data);
    } catch (error) {
      console.error("Error fetching weather data", error);
    }
  };

  const requestChangeRoute = async () => {
    try {
      await axios.post(`${config.backendUrl}/request-change-route`, {
        current_coords: currentCoords,
        planned_route: route,
        destination_airport_id: destinationAirportId
      });
      alert('Change route request sent to ATC.');
    } catch (error) {
      console.error("Error sending change route request", error);
    }
  };

  const createCustomMarkerIcon = () => {
    return new L.Icon({
      iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
      iconSize: [25, 41],
      iconAnchor: [12, 41],
      popupAnchor: [1, -34],
      shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
      shadowSize: [41, 41]
    });
  };

  const fetchApprovedRoute = async () => {
    try {
      const response = await axios.get(`${config.backendUrl}/approved-route`, {
        params: { destination_airport_id: destinationAirportId }
      });
      if (response.data.route) {
        setApprovedRoute(response.data.route);
      } else {
        console.error("Error fetching approved route: No route found");
      }
    } catch (error) {
      console.error("Error fetching approved route", error);
    }
  };

  useEffect(() => {
    fetchApprovedRoute();
  }, []);

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Pilot Interface</h1>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Route</h2>
          <MapContainer center={[currentCoords.lat, currentCoords.lon]} zoom={5} style={{ height: '400px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {route.length > 0 && (
              <>
                <Polyline
                  positions={route.map(coord => [coord.lat, coord.lon])}
                  color="blue"
                />
                {route.map((coord, index) => (
                  <Marker key={index} position={[coord.lat, coord.lon]} icon={createCustomMarkerIcon()}>
                    <Popup>
                      {index === 0 ? `Source Airport: ${route[0].name}` : index === route.length - 1 ? `Destination Airport: ${route[route.length - 1].name}` : `Waypoint ${index}`}
                    </Popup>
                  </Marker>
                ))}
              </>
            )}
            {approvedRoute && (
              <Polyline
                positions={approvedRoute.map(coord => [coord.lat, coord.lon])}
                color="green"
              />
            )}
          </MapContainer>
          <button
            className="btn btn-primary mt-4"
            onClick={requestChangeRoute}
          >
            Change Route
          </button>
        </div>
      </div>

      {weather && (
        <div className="card mb-4">
          <div className="card-body">
            <h2 className="card-title">Current Weather</h2>
            <p><strong>Temperature:</strong> {weather.main.temp} K</p>
            <p><strong>Weather:</strong> {weather.weather[0].description}</p>
            <p><strong>Humidity:</strong> {weather.main.humidity}%</p>
            <p><strong>Wind Speed:</strong> {weather.wind.speed} m/s</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
