import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { MapContainer, TileLayer, Marker, Polyline, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

const createCustomMarkerIcon = () => {
  return new L.Icon({
    iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    shadowSize: [41, 41]
  });
};

const App = () => {
  const [airlines, setAirlines] = useState([]);
  const [airports, setAirports] = useState([]);
  const [selectedAirline, setSelectedAirline] = useState('');
  const [sourceAirport, setSourceAirport] = useState('');
  const [destinationAirport, setDestinationAirport] = useState('');
  const [route, setRoute] = useState([]);
  const [currentCoords, setCurrentCoords] = useState({ lat: 0, lon: 0 });
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const airlinesResponse = await axios.get('http://localhost:5000/api/airlines');
      const airportsResponse = await axios.get('http://localhost:5000/api/airports');
      setAirlines(airlinesResponse.data);
      setAirports(airportsResponse.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const fetchWeather = async (lat, lon) => {
    try {
      const response = await axios.get('http://localhost:5000/api/weather', {
        params: { lat, lon }
      });
      setWeather(response.data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  const handleFindRoute = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/route_by_airline', {
        params: {
          airline_id: selectedAirline,
          source_airport_id: sourceAirport,
          destination_airport_id: destinationAirport
        }
      });
      setRoute(response.data.waypoints);
    } catch (error) {
      console.error('Error finding route:', error);
    }
  };

  const requestAlternativeRoute = async () => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/api/request-change-route', {
        current_coords: currentCoords,
        planned_route: route
      });
      const newRoute = response.data.new_route;
      setRoute(newRoute);
      setLoading(false);
    } catch (error) {
      console.error('Error requesting alternative route:', error);
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1 className="my-4">Pilot Interface</h1>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Route Information</h2>
          <div className="form-group">
            <label htmlFor="selectedAirline">Airline</label>
            <select
              id="selectedAirline"
              className="form-control"
              value={selectedAirline}
              onChange={(e) => setSelectedAirline(e.target.value)}
            >
              <option value="">Select Airline</option>
              {airlines.map((airline) => (
                <option key={airline['Airline ID']} value={airline['Airline ID']}>
                  {airline.Name}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="sourceAirport">Source Airport</label>
            <select
              id="sourceAirport"
              className="form-control"
              value={sourceAirport}
              onChange={(e) => setSourceAirport(e.target.value)}
            >
              <option value="">Select Source Airport</option>
              {airports.map((airport) => (
                <option key={airport['Airport ID']} value={airport['Airport ID']}>
                  {airport.Name}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="destinationAirport">Destination Airport</label>
            <select
              id="destinationAirport"
              className="form-control"
              value={destinationAirport}
              onChange={(e) => setDestinationAirport(e.target.value)}
            >
              <option value="">Select Destination Airport</option>
              {airports.map((airport) => (
                <option key={airport['Airport ID']} value={airport['Airport ID']}>
                  {airport.Name}
                </option>
              ))}
            </select>
          </div>
          <button className="btn btn-primary mt-4" onClick={handleFindRoute}>
            Find Route
          </button>
        </div>
      </div>

      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Current Location</h2>
          <MapContainer center={[currentCoords.lat, currentCoords.lon]} zoom={5} style={{ height: '400px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {route.length > 0 && (
              <>
                <Polyline
                  positions={route.map((coord) => [coord.lat, coord.lon])}
                  color="blue"
                />
                {route.map((coord, index) => (
                  <Marker key={index} position={[coord.lat, coord.lon]} icon={createCustomMarkerIcon()}>
                    <Popup>
                      {index === 0 ? coord.name : index === route.length - 1 ? coord.name : `Waypoint ${index}`}
                    </Popup>
                  </Marker>
                ))}
              </>
            )}
          </MapContainer>
          <button
            className="btn btn-primary mt-4"
            onClick={requestAlternativeRoute}
            disabled={loading}
          >
            Request Alternative Route
          </button>
        </div>
      </div>

      {weather && (
        <div className="card mb-4">
          <div className="card-body">
            <h2 className="card-title">Current Weather</h2>
            <p><strong>Temperature:</strong> {weather.main.temp} K</p>
            <p><strong>Weather:</strong> {weather.weather[0].description}</p>
            <p><strong>Humidity:</strong> {weather.main.humidity}%</p>
            <p><strong>Wind Speed:</strong> {weather.wind.speed} m/s</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
