import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { MapContainer, TileLayer, Marker, Polyline, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

const createCustomMarkerIcon = () => {
  return new L.Icon({
    iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    shadowSize: [41, 41]
  });
};

const App = () => {
  const [routes, setRoutes] = useState([]);
  const [changeRequests, setChangeRequests] = useState([]);
  const [approvedRoute, setApprovedRoute] = useState(null);

  useEffect(() => {
    fetchRoutes();
    fetchChangeRequests();
  }, []);

  const fetchRoutes = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/all_routes', {
        params: {
          page: 1,
          per_page: 10
        }
      });
      setRoutes(response.data.routes);
    } catch (error) {
      console.error('Error fetching routes', error);
    }
  };

  const fetchChangeRequests = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/change-requests');
      setChangeRequests(response.data.change_requests);
    } catch (error) {
      console.error('Error fetching change requests', error);
    }
  };

  const approveRouteChange = async (requestId) => {
    try {
      const response = await axios.post('http://localhost:5000/api/approve-change-route', { request_id: requestId });
      setApprovedRoute(response.data.approved_route);
    } catch (error) {
      console.error('Error approving route change', error);
    }
  };

  return (
    <div className="container">
      <h1 className="my-4">ATC Interface</h1>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Routes</h2>
          <MapContainer center={[0, 0]} zoom={2} style={{ height: '400px' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {routes.map((route, index) => (
              <Polyline
                key={index}
                positions={[
                  [route.source_coords.lat, route.source_coords.lon],
                  [route.destination_coords.lat, route.destination_coords.lon]
                ]}
                color="blue"
              >
                <Popup>
                  Source: {route.source_name}<br />
                  Destination: {route.destination_name}
                </Popup>
              </Polyline>
            ))}
            {approvedRoute && (
              <Polyline
                positions={approvedRoute.map((coord) => [coord.lat, coord.lon])}
                color="green"
              >
                <Popup>
                  Optimized Route
                </Popup>
              </Polyline>
            )}
          </MapContainer>
        </div>
      </div>
      <div className="card mb-4">
        <div className="card-body">
          <h2 className="card-title">Change Requests</h2>
          <ul className="list-group">
            {changeRequests.map((request) => (
              <li key={request._id} className="list-group-item d-flex justify-content-between align-items-center">
                <span>
                  From: {request.source_name} To: {request.destination_name}
                </span>
                <button
                  className="btn btn-primary"
                  onClick={() => approveRouteChange(request._id)}
                >
                  Approve
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default App;
