// pilot-interface/src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import config from './config'; // Import the configuration

const App = () => {
  const [location, setLocation] = useState({ lat: 0, lon: 0 });
  const [weather, setWeather] = useState({});
  const [route, setRoute] = useState([]);

  useEffect(() => {
    fetchWeather();
  }, [location]);

  const fetchWeather = async () => {
    try {
      const response = await axios.get(`${config.backendUrl}/weather?lat=${location.lat}&lon=${location.lon}`);
      setWeather(response.data);
    } catch (error) {
      console.error("Error fetching weather data", error);
    }
  };

  const requestAlternativeRoute = async () => {
    try {
      const response = await axios.post(`${config.backendUrl}/alternative-route`, { current_coords: location, planned_route: route });
      setRoute(response.data.route);
    } catch (error) {
      console.error("Error fetching alternative route", error);
    }
  };

  return (
    <div>
      <h1>Pilot Interface</h1>
      <div>
        <h2>Current Location</h2>
        <p>Latitude: {location.lat}</p>
        <p>Longitude: {location.lon}</p>
      </div>
      <div>
        <h2>Weather</h2>
        <pre>{JSON.stringify(weather, null, 2)}</pre>
      </div>
      <div>
        <h2>Route</h2>
        <pre>{JSON.stringify(route, null, 2)}</pre>
      </div>
      <button onClick={requestAlternativeRoute}>Request Alternative Route</button>
    </div>
  );
};

export default App;
